mapper.select:
select tmp.workshop,group_concat(tmp.month,':',tmp.quantity separator ',') output_info from( select '自制件下线' workshop,ifnull(sum(s.quantity),0) quantity,substring(s.supply_date,6,2) month from BMS_PD_WORKSHOP_SUPPLY s where s.supply_workshop='自制件' and s.receive_workshop='部件'
and s.factory_id=#{factory_id}
and s.supply_date like concat(#{year},'%') group by substring(s.supply_date,1,7) union all select '部件下线' workshop,ifnull(sum(f.offline_real_qty),0) quantity,substring(f.prod_date,6,2) month from BMS_PD_PARTS_PLAN_FINISH f left join BMS_BASE_KEY p1 on p1.value=f.parts_id AND p1.key_code ='BASE_PARTS' where p1.key_name='喷砂' and f.prod_date like concat(#{year},'%')
and f.factory_id=#{factory_id}
group by substring(f.prod_date,1,7) union all select '焊装上线' workshop,count(b.bus_number) quanity,substring(b.welding_online_date,6,2) month from BMS_PLAN_BUS b where b.welding_online_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.welding_online_date,1,7) union all select '焊装下线' workshop,count(b.bus_number) quanity,substring(b.welding_offline_date,6,2) month from BMS_PLAN_BUS b where b.welding_offline_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.welding_offline_date,1,7) union all select '涂装上线' workshop,count(b.bus_number) quanity,substring(b.painting_online_date,6,2) month from BMS_PLAN_BUS b where b.painting_online_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.painting_online_date,1,7) union all select '涂装下线' workshop,count(b.bus_number) quanity,substring(b.painting_offline_date,6,2) month from BMS_PLAN_BUS b where b.painting_offline_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.painting_offline_date,1,7) union all select '底盘上线' workshop,count(b.bus_number) quanity,substring(b.chassis_online_date,6,2) month from BMS_PLAN_BUS b where b.chassis_online_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.chassis_online_date,1,7) union all select '底盘下线' workshop,count(b.bus_number) quanity,substring(b.chassis_offline_date,6,2) month from BMS_PLAN_BUS b where b.chassis_offline_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.chassis_offline_date,1,7) union all select '总装上线' workshop,count(b.bus_number) quanity,substring(b.assembly_online_date,6,2) month from BMS_PLAN_BUS b where b.assembly_online_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.assembly_online_date,1,7) union all select '总装下线' workshop,count(b.bus_number) quanity,substring(b.assembly_offline_date,6,2) month from BMS_PLAN_BUS b where b.assembly_offline_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.assembly_offline_date,1,7) union all select '入库' workshop,count(b.bus_number) quanity,substring(b.warehousing_date,6,2) month from BMS_PLAN_BUS b where b.warehousing_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.warehousing_date,1,7) union all select '发车' workshop,count(b.bus_number) quanity,substring(b.dispatch_date,6,2) month from BMS_PLAN_BUS b where b.dispatch_date like concat(#{year},'%')
and b.factory_id=#{factory_id}
group by substring(b.dispatch_date,1,7) ) tmp group by tmp.workshop;

select id,factory_name, sum(case key_name when '焊装上线' then plan_qty else 0 end) welding_online, ( select count(id) from BMS_PLAN_BUS b1 where b1.factory_id=c.id and b1.welding_online_date>CONCAT(date_add(#{start_date},interval '-1' DAY),' 17:30:00') AND b1.welding_online_date<=CONCAT(#{end_date},' 17:30:00')) welding_offline, sum(case key_name when '底盘上线' then plan_qty else 0 end) chassis_online, (select count(id) from BMS_PLAN_BUS b2 where b2.factory_id=c.id and b2.chassis_online_date>CONCAT(date_add(#{start_date},interval '-1' DAY),' 17:30:00') AND b2.chassis_online_date<=CONCAT(#{end_date},' 17:30:00')) chassis_offline from ( select a.id,a.factory_name,a.key_name,sum(plan_qty) plan_qty,p.plan_date from ( ( select f.id,f.factory_name,k.key_name,k.value from BMS_BASE_FACTORY f,BMS_BASE_KEY k where key_code = 'PLAN_CODE' and k.value in (4,8) and capacity>0 ) a LEFT JOIN BMS_PLAN_PRODUCTION_PLAN p on a.`value`=p.plan_code_value and a.id=p.factory_id and plan_date>=#{start_date} and plan_date<=#{end_date} ) GROUP BY id,factory_name,key_name ) c where 1=1
and factory_name=#{factory}
GROUP BY factory_name order by id
